select b.NERVEID as PERSONID ,max(case PARA3 when 'nx' THEN 1 else 0 end) as NX ,max(case PARA3 when 'dd' THEN 1 else 0 end) as DD ,max(case PARA3 when 'hy' THEN 1 else 0 end) as HY ,max(case PARA3 when 'zj' THEN 1 else 0 end) as ZJ ,max(case PARA3 when 'zlcw' THEN 1 else 0 end) as ZLCW from WEBTV.EB_PERSON_OP_LOG a inner join WEBTV.eb_mobile_reg_log b on a.personid=B.PERSONID where OPTYPE = 42 and b.nerveid in
${mapV.personId}
GROUP BY b.NERVEID

  SELECT t.*
	FROM WEBTV.EB_PERSON_BEHAVIOUR_LOG t
  where t.LOGTIME >= #{sDate,jdbcType=TIMESTAMP}
  and t.LOGTIME<=#{eDate,jdbcType=TIMESTAMP}

  SELECT t.*
	FROM WEBTV.EB_PERSON_BEHAVLOG_9 t
  where t.LOGTIME >= #{sDate,jdbcType=TIMESTAMP}
  and t.LOGTIME<=#{eDate,jdbcType=TIMESTAMP}

  	and (

  	param3 in(${qparam3}) 

  	or S3 in(${qs3})

  	)

  SELECT t.*
	FROM WEBTV.EB_PERSON_BEHAVLOG_10 t
  where t.LOGTIME >= #{sDate,jdbcType=TIMESTAMP}
  and t.LOGTIME<=#{eDate,jdbcType=TIMESTAMP}

  	SELECT	USERID,STATDATE,CALLOUTCOST,CALLOUT,TO_CHAR(STATDATE,'yyyyMMdd') as SDATE
	FROM (
	  SELECT t.USERID,	t.STATDATE,	t.CALLOUTCOST,	t.CALLOUT ,ROWNUM as nRowId
		FROM WEBTV.EB_SALES_REL_TELALLOC_STAT t
	  where STATDATE >= to_date(#{sDate,jdbcType=INTEGER},'YYYY-MM-DD') and USERID>=#{nUserId,jdbcType=INTEGER} 
	  ORDER BY STATDATE ASC,USERID ASC
	) ab where ab.nRowId<=#{nRowCount,jdbcType=INTEGER}

  SELECT distinct t.*,l.NERVEID 
	FROM WEBTV.EB_PERSON_QUESTION t
	inner join WEBTV.EB_PERSON_REG_LOG l on t.PERSONID=l.PERSONID	
  where t.CREATETIME >= #{sDate,jdbcType=TIMESTAMP}
	and t.CREATETIME<=#{eDate,jdbcType=TIMESTAMP}


  SELECT distinct t.*,l.NERVEID
	FROM WEBTV.EB_PERSON_QUESTION_LOG t
	inner join  WEBTV.EB_PERSON_REG_LOG l on t.PERSONID=l.PERSONID
  where t.LOGTIME >= #{sDate,jdbcType=TIMESTAMP}
  	and t.LogTime<=#{eDate,jdbcType=TIMESTAMP}

SELECT distinct b.NerveId as PERSONID,a.PERSONID as NerveId,a.OLDKEEPERID,a.NEWKEEPERID,a.LOGTIME,a.OLDKEEPERGID,a.OPTYPE
	,a.NEWKEEPERGID,a.COMMENTS,a.OLDKEEPERNAME,a.NEWKEEPERNAME,a.VALID
FROM WEBTV.EB_PERSON_KEEPER_LOG a
INNER JOIN WEBTV.EB_MOBILE_REG_LOG b on a.PersonId=b.PersonId
WHERE a.LogTime>=#{sDate,jdbcType=TIMESTAMP} 
	and a.LogTime<=#{eDate,jdbcType=TIMESTAMP}

SELECT distinct * FROM ( SELECT A.PERSONID,A.LOGTIME,B.NERVEID,A.KEEPERID,A.CUSTESTIMATE,0 AS A0,A.BOUGHT,A.PREALLOCGID,a.VERSION FROM webtv.EB_PERSON_PRESALE_LOG a LEFT JOIN WEBTV.eb_mobile_reg_log b ON a.personid = b.personid WHERE
a.LOGDATE > to_date(to_char(sysdate-2,'yyyy-MM-dd'),'yyyy-MM-dd')
a.LOGTIME >=#{sDate,jdbcType=TIMESTAMP} AND a.LOGTIME <#{eDate,jdbcType=TIMESTAMP}
and a.custestimate in ('B','B0','BPLUS','B1','B2','B3','C') UNION ALL SELECT A.PERSONID,A.LOGTIME,B.NERVEID,A.KEEPERID,A.CUSTESTIMATE,0 AS A0,A.BOUGHT,A.PREALLOCGID,101 as VERSION FROM webtv.EB_PERSON_PRESALE_LOG a LEFT JOIN WEBTV.eb_mobile_reg_log b ON a.personid = b.personid WHERE a.VERSION = 102 and a.LOGDATE > to_date(to_char(sysdate-2,'yyyy-MM-dd'),'yyyy-MM-dd')
AND a.LOGTIME >=#{sDate,jdbcType=TIMESTAMP} AND a.LOGTIME <#{eDate,jdbcType=TIMESTAMP}
and a.custestimate in ('A0') and comments like '%新增A0%' ) a


    SELECT NDATE,PERSONID,NID,LOGINDAYS,NEWSARTICLECNT,ROOMDAYS,CALLINCNT,MODIFYTIME
	FROM WEBTV.EB_FLASH_MATUREVALUE a 
	WHERE NDATE=#{sDate,jdbcType=INTEGER}
